import React, { useState } from "react";
import axios from "axios";

function App() {
  const [url, setUrl] = useState("");
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  const checkUrl = async () => {
    if (!url.trim()) {
      setError("Please enter a valid URL");
      return;
    }

    setError("");
    setLoading(true);
    setResult(null);

    try {
      const response = await axios.get(
        `http://127.0.0.1:8000/predict-url?url=${encodeURIComponent(url)}`
      );

      setResult(response.data.prediction);
    } catch (err) {
      setError("Backend connection failed. Is FastAPI running?");
    }

    setLoading(false);
  };

  return (
    <div style={styles.container}>
      <h1 style={styles.title}>Phishing URL Detector (AI/ML)</h1>

      <input
        type="text"
        placeholder="Enter website URL..."
        value={url}
        onChange={(e) => setUrl(e.target.value)}
        style={styles.input}
      />

      <button onClick={checkUrl} style={styles.button}>
        {loading ? "Checking..." : "Check URL"}
      </button>

      {error && <p style={styles.error}>{error}</p>}

      {result && (
        <div
          style={{
            ...styles.resultBox,
            backgroundColor: result === "Phishing" ? "#ffcccc" : "#ccffcc",
            borderColor: result === "Phishing" ? "#ff0000" : "#00aa00",
          }}
        >
          <h2 style={styles.resultText}>
            Result: <strong>{result}</strong>
          </h2>
        </div>
      )}
    </div>
  );
}

const styles = {
  container: {
    fontFamily: "Arial",
    padding: "40px",
    maxWidth: "600px",
    margin: "auto",
    textAlign: "center",
  },
  title: {
    marginBottom: "20px",
  },
  input: {
    width: "100%",
    padding: "12px",
    fontSize: "16px",
    borderRadius: "8px",
    border: "1px solid #aaa",
    marginBottom: "20px",
  },
  button: {
    padding: "12px 25px",
    fontSize: "16px",
    borderRadius: "8px",
    border: "none",
    backgroundColor: "#007bff",
    color: "white",
    cursor: "pointer",
  },
  error: {
    marginTop: "20px",
    color: "red",
    fontWeight: "bold",
  },
  resultBox: {
    marginTop: "30px",
    padding: "20px",
    borderRadius: "10px",
    border: "2px solid",
  },
  resultText: {
    margin: 0,
    fontSize: "22px",
  },
};

export default App;
