import React, { useState } from "react";
import axios from "axios";

function App() {
  const [url, setUrl] = useState("");
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  const scanUrl = async () => {
    if (!url.trim()) {
      setError("Please enter a valid URL.");
      return;
    }

    setError("");
    setResult(null);
    setLoading(true);

    try {
      const res = await axios.get(
        `http://127.0.0.1:8000/predict-url?url=${encodeURIComponent(url)}`
      );

      setResult(res.data);
    } catch (err) {
      console.error(err);
      setError("⚠ Backend not responding. Is FastAPI running?");
    }

    setLoading(false);
  };

  return (
    <div style={styles.container}>
      <h1 style={styles.title}>AI/ML Phishing URL Detector</h1>

      <input
        type="text"
        placeholder="Enter URL here..."
        value={url}
        onChange={(e) => setUrl(e.target.value)}
        style={styles.input}
      />

      <button onClick={scanUrl} style={styles.button}>
        {loading ? "Scanning..." : "Scan URL"}
      </button>

      {error && <p style={styles.error}>{error}</p>}

      {result && (
        <div
          style={{
            ...styles.resultBox,
            backgroundColor:
              result.final_prediction === "Phishing" ? "#ffcccc" : "#ccffcc",
            borderColor:
              result.final_prediction === "Phishing" ? "#cc0000" : "#009900",
          }}
        >
          <h2>
            Final Result:{" "}
            <span style={{ fontWeight: "bold" }}>
              {result.final_prediction}
            </span>
          </h2>

          <p>ML Model Prediction: {result.ml_prediction}</p>
          <p>Rule-Based Score: {result.rule_score}</p>
        </div>
      )}
    </div>
  );
}

const styles = {
  container: {
    fontFamily: "Arial",
    padding: "40px",
    maxWidth: "600px",
    margin: "auto",
    textAlign: "center",
  },
  title: {
    marginBottom: "20px",
    fontSize: "26px",
  },
  input: {
    width: "100%",
    padding: "12px",
    fontSize: "16px",
    borderRadius: "8px",
    border: "1px solid #aaa",
    marginBottom: "20px",
  },
  button: {
    padding: "12px 22px",
    fontSize: "17px",
    backgroundColor: "#007bff",
    color: "white",
    border: "none",
    borderRadius: "8px",
    cursor: "pointer",
  },
  error: {
    marginTop: "20px",
    color: "red",
    fontWeight: "bold",
  },
  resultBox: {
    marginTop: "30px",
    padding: "20px",
    borderRadius: "10px",
    border: "2px solid",
    textAlign: "left",
  },
};

export default App;
