import React, { useState } from "react";
import axios from "axios";

function App() {
  const [input, setInput] = useState("");
  const [result, setResult] = useState(null);

  const handlePredict = async () => {
    try {
      const featureArray = input.split(",").map(Number);

      if (featureArray.length !== 48) {
        alert("You must enter exactly 48 feature values!");
        return;
      }

      const res = await axios.post("http://127.0.0.1:8000/predict", {
        features: featureArray,
      });

      const label = res.data.prediction === 1 ? "Phishing" : "Legitimate";

      setResult(label);
    } catch (err) {
      console.error(err);
      alert("Error calling backend API.");
    }
  };

  return (
    <div style={{ padding: "30px", fontFamily: "Arial" }}>
      <h1>Phishing Detection System</h1>

      <textarea
        rows={5}
        style={{ width: "100%", marginTop: "20px" }}
        placeholder="Enter 48 comma-separated feature values"
        onChange={(e) => setInput(e.target.value)}
      />

      <button
        onClick={handlePredict}
        style={{
          marginTop: "20px",
          padding: "10px 20px",
          fontSize: "16px",
          cursor: "pointer",
        }}
      >
        Predict
      </button>

      {result && (
        <h2 style={{ marginTop: "20px" }}>
          Prediction: <span>{result}</span>
        </h2>
      )}
    </div>
  );
}

export default App;
