import React, { useState } from "react";
import axios from "axios";

function App() {
  const [url, setUrl] = useState("");
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  const checkUrl = async () => {
    if (!url) {
      setError("Please enter a URL");
      return;
    }

    setLoading(true);
    setError("");
    setResult(null);

    try {
      const res = await axios.get(
        `http://127.0.0.1:8000/predict-url?url=${encodeURIComponent(url)}`
      );

      setResult(res.data);
    } catch (err) {
      setError("Backend not reachable or CORS issue");
    }

    setLoading(false);
  };

  return (
    <div style={styles.container}>
      <h1 style={styles.title}>üîç AI Phishing URL Detector</h1>

      <input
        type="text"
        placeholder="Enter URL to check..."
        value={url}
        onChange={(e) => setUrl(e.target.value)}
        style={styles.input}
      />

      <button onClick={checkUrl} style={styles.button}>
        Check URL
      </button>

      {loading && <p style={styles.loading}>Checking URL...</p>}
      {error && <p style={styles.error}>{error}</p>}

      {result && (
        <div style={styles.card}>
          <h2>
            Result:{" "}
            <span
              style={{
                color: result.final_prediction === "Phishing" ? "red" : "green",
              }}
            >
              {result.final_prediction}
            </span>
          </h2>

          <p>
            <b>ML Prediction:</b> {result.ml_prediction}  
            <span style={{ color: "gray" }}>
              {result.ml_prediction === 1 ? " (Phishing)" : " (Legitimate)"}
            </span>
          </p>

          <p>
            <b>Rule Score:</b> {result.rule_score}
          </p>

          <details style={styles.details}>
            <summary>Show 48 ML Features</summary>
            <pre style={styles.pre}>{JSON.stringify(result.features_used, null, 2)}</pre>
          </details>
        </div>
      )}
    </div>
  );
}

const styles = {
  container: {
    fontFamily: "Arial, sans-serif",
    textAlign: "center",
    padding: 30,
  },
  title: {
    fontSize: 32,
    marginBottom: 20,
  },
  input: {
    width: "60%",
    padding: 10,
    fontSize: 18,
    border: "2px solid #888",
    borderRadius: 5,
  },
  button: {
    marginLeft: 10,
    padding: "10px 20px",
    fontSize: 18,
    cursor: "pointer",
    background: "#007bff",
    color: "white",
    border: "none",
    borderRadius: 5,
  },
  loading: { fontSize: 16, color: "orange" },
  error: { fontSize: 16, color: "red" },
  card: {
    marginTop: 30,
    padding: 20,
    border: "1px solid #ccc",
    borderRadius: 10,
    width: "60%",
    marginLeft: "auto",
    marginRight: "auto",
    background: "#f8f8f8",
  },
  details: { marginTop: 15, textAlign: "left" },
  pre: {
    background: "#222",
    color: "#0f0",
    padding: 10,
    borderRadius: 8,
    overflowX: "scroll",
  },
};

export default App;
